import React from 'react';
import './Header.css';
// import SearchIcon from '@mui/icons-material/Search';
import SearchIcon from '@material-ui/icons/Search';
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
import { Link } from "react-router-dom"
import { useStateValue } from "./StateProvider"
import { auth } from "./firebase"

function Header() {

    const [{ basket, user }, dispatch] = useStateValue()

    const handleAuthentification = () => {
        if (user) {
            auth.signOut();
        }
    }

    return (
        <div className='header'>
            <Link to="/">
                <img className="header__logo" src="http://pngimg.com/uploads/amazon/amazon_PNG11.png"/>
            </Link>

            <div className = "header__search">
                <input className = "header__searchInput" type="text" />
                <SearchIcon className = "header__searchIcon" />
            </div>

            <div className="header__nav">
                <Link to={!user && '/login'}>
                    <div onClick={handleAuthentification} className="header__option">
                        <span className="header__optionLineOne">Hello Guest</span>
                        <span
                        className="header__optionLineTwo">{user ? 'Sign out' : 'Sign in'}</span>
                    </div>
                </Link>

                <div className="header__option">
                    <span className="header__optionLineOne">Returns</span>
                    <span
                    className="header__optionLineTwo">& Orders</span>
                </div>

                <div className="header__option">
                    <span       className="header__optionLineOne">Your</span>
                    <span
                    className="header__optionLineTwo"> Prime</span>
                </div>

                <Link to='/checkout'>
                    <div className="header__optionBasket">
                        <ShoppingBasketIcon />
                        <span className="header__optionLineTwo header__basketCount">{basket?.length}</span>
                    </div>
                </Link>

            </div>
        </div>
    )
}

export default Header
